<?php\n/**\n * Sitemap Generator\n * \n * Generates XML sitemap for search engine optimization\n */\n\nrequire_once 'db.php';\n\nheader('Content-Type: application/xml; charset=utf-8');\n\n$domain = 'https://' . $_SERVER['HTTP_HOST'];\n\n// Get all manga\n$sql = \"SELECT id, last_chapter FROM manga ORDER BY id DESC\";\n$result = $conn->query($sql);\n\n$urls = [\n    ['loc' => $domain . '/', 'priority' => '1.0', 'changefreq' => 'daily'],\n    ['loc' => $domain . '/browse.php', 'priority' => '0.9', 'changefreq' => 'daily'],\n    ['loc' => $domain . '/login_fixed.php', 'priority' => '0.8', 'changefreq' => 'monthly'],\n    ['loc' => $domain . '/register.php', 'priority' => '0.8', 'changefreq' => 'monthly'],\n];\n\nif ($result && $result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        $urls[] = [\n            'loc' => $domain . '/user_progress.php?id=' . $row['id'],\n            'priority' => '0.7',\n            'changefreq' => 'weekly'\n        ];\n    }\n}\n\n$conn->close();\n\necho '<?xml version=\"1.0\" encoding=\"UTF-8\"?>' . \"\\n\";\necho '<urlset xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\">' . \"\\n\";\n\nforeach ($urls as $url) {\n    echo '  <url>' . \"\\n\";\n    echo '    <loc>' . htmlspecialchars($url['loc']) . '</loc>' . \"\\n\";\n    echo '    <changefreq>' . $url['changefreq'] . '</changefreq>' . \"\\n\";\n    echo '    <priority>' . $url['priority'] . '</priority>' . \"\\n\";\n    echo '  </url>' . \"\\n\";\n}\n\necho '</urlset>' . \"\\n\";\n\n?>\n"